import { Component } from '@angular/core';
import {Categories} from "./categories";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  categories = [...Categories]
  category = false;
  changeCategory(){
    this.category = !this.category;
  }
}


