import {Component, EventEmitter, Input, Output} from '@angular/core';
import {Product, products, ProductClass} from "../products";

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent {
  @Input() product: Product | undefined;
  @Output() remove = new EventEmitter();

  Remove(){
    this.remove.emit(this.product?.id);
  }
  like(){
    if(this.product){
      this.product.likes += 1;
    }
  }
}
<div *ngIf="product" class="item">
    <h3 class="text">
        <a href="{{ product.shopLink }} " target="_blank">
            {{ product.name }}
        </a>
    </h3>
    <img src="{{ product.img }}">
    <p *ngIf="product.description" class="text">
        Описание: {{ product.description }}
    </p>
    <p>
        Цена: {{ product.price | currency: 'KZT' }}
    </p>
    <p>
        Рейтинг: {{ product.rating }}
    </p>
    <a href="https://wa.me/77012465854?text=Здравствуйте, хотел%20бы%20приобрести%20этот%20товар: {{ product.shopLink }}" class="share" target="_blank">
        <button type="button">
            Поделиться
        </button>
    </a>
    <button type="button" class="remove" (click)="Remove()">
        Удалить
    </button>
    <div class="like">
        <img src="https://cdn-icons-png.flaticon.com/512/20/20664.png" alt="like" class="like-photo" (click)="like()">
        <span>{{ product.likes }}</span>
    </div>
</div>
